    font-family: 'Bebas Neue', sans-serif;
    font-size: 44px;
}
h2 {
    font-family: 'Bebas Neue', sans-serif;
    font-size: 38px;
}
h3 {
    font-family: 'Bebas Neue', sans-serif;
    font-size: 32px;
}
h4 {
    font-family: 'Bebas Neue', sans-serif;
    font-size: 28px;
}
h5 {
    font-family: 'Bebas Neue', sans-serif;
    font-size: 24px;
}
.grid-container {
    display: grid;
    grid-template-columns: 49% 49%;
    column-gap: 18px;
}
.grid-item {
    text-align: left;
}
.btn-success {
    padding: 5px 10px 6px 10px;
    font-size: 14px;
    margin-top: -13px;
