    padding: 4px 0px 4px 0px;
}
.fancy-heading {
    padding: 0px 0px 10px 0px;
}
.button-download {
    background-color: #00abba;
    border-radius: 4px;
    text-decoration: none;
    color: #fff;
    padding: 8px 11px 8px 11px;
}
.button-download:hover {
    background-color: #65b5bc;
    text-decoration: none;
    color: #fff;
