    border-radius: 4px;
    text-decoration: none;
    color: #31708f;
    padding: 8px 11px 8px 11px;
}
.button-fancy:hover {
    background-color: #bad5ed;
    text-decoration: none;
    color: #000;
}
.button-fancy i {
    color: #31708f;
}
.news-container {
    padding: none;
}
.news-title, a.news-link {
    color: #000;
    text-decoration: none;
}
.news-title, a.news-link:hover {
    color: #00abba;
    text-decoration: none;
}
.signup-link {
    font-size: 28px;
    font-weight: 800;
    font-family: 'Bebas Neue', sans-serif;
    font-size: 32px;
}
.socials-heading {
    color: #00abba;
}
/style
